mapper.select:
SELECT
	*
FROM
	tbl_orderdetail
WHERE
	customerNo =  #{customerNo}
	AND orderNo = #{orderNo}
	AND productCode = #{productCode}

mapper.update:
UPDATE
	tbl_orderdetail

	quantityOrdered = #{quantityOrdered}, 
	priceOrdered = #{priceOrdered}, 

WHERE
	customerNo =  #{customerNo}
	AND orderNo = #{orderNo}
	AND productCode = #{productCode}

mapper.delete:
DELETE FROM
		tbl_orderdetail
WHERE
	customerNo =  #{customerNo}
	AND orderNo = #{orderNo}
	AND productCode = #{productCode}
