mapper.update:
UPDATE tbl_order
branchNo = #{branchNo}, deliveryDestNo = #{deliveryDestNo}, shippingLabelNo = #{shippingLabelNo}, estimationNo = #{estimationNo}, memo = #{memo}, estimationDate = #{estimationDate}, orderDate = #{orderDate}, registrationDate = #{registrationDate}, shippingDate = #{shippingDate}, deliveryDate = #{deliveryDate}, invoiceDate = #{invoiceDate}, requiredDate = #{requiredDate}, status = #{status},
WHERE customerNo = #{customerNo} AND orderNo = #{orderNo}

mapper.delete:
DELETE FROM
		tbl_order
WHERE
	customerNo =  #{customerNo}
	AND orderNo =  #{orderNo}
